                            && targetItem->value30 == SET_VALUE
                            */

                            )
                        {

                        }
                        else {
                            //FAIL();
                        }
                    }


                    if (targetItem->value15 != SET_VALUE) {
                        i++;

                        {
                            int try_n = 0;
                            volatile int vi = 0;
                            while (true) {
                                vi++;

                                if (ARM) {
                                    wow2.store(!wow2.load(), std::memory_order_relaxed);
                                }
                                else {
                                    do_pause_or_nothing();
                                }

                                if (SET_VALUE == targetItem->value15) {
                                    i--;
                                    break;
                                }

                                try_n++;
                                if (try_n > 300) {
                                    int try_n2 = 0;
                                    while (true) {
                                        std::this_thread::sleep_for(
                                            std::chrono::milliseconds{ 0 }
                                        );
                                        if (SET_VALUE == targetItem->value15) {
                                            i--;
