    std::string text = u8R"(
class TestClüòÇÊó•Êú¨Ë™û10234ass {


}




class a {

}
class agiplkmp {

}

class jips {

}




  )";

    unsigned long long count = 0;
    const char *chars = text.c_str();
    char ch = 'a';
    for (unsigned long long i = 0; i < loopCount; i++) {
        if (i > 5) {
            for (int_fast32_t k = 0; k < text.size(); k++) {
                ch = chars[k];
                if (ch == ' ') {
                    count = i;
